var highlighter = new Highlighter({
	// canvas and element - whole - container
	container: 'body',

	// element selector
	element : '.container',

	// canvas z-index , default equals 10
	zIndex : '3',

	// default equals 10
	calibarationLevel: 10,

	// default equals 'e'
	lang: 'e',

	// default equals 10
	radius: 10,

	// highlighter opacity - default equal 0.5
	opacity: 0.5
});